import React, { useState, useContext , Component } from "react";
import {
  AppBar,
  Toolbar,
  IconButton,
  MenuItem,
  Menu,
  Button,
  Typography,
} from "@material-ui/core";
import Toast from 'react-bootstrap/Toast';
import Modal from 'react-bootstrap/Modal';

import ToastHeader from 'react-bootstrap/ToastHeader';
import ToastBody from 'react-bootstrap/ToastBody';
import { Col, Row, Form  } from "react-bootstrap";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import VideoCallIcon from "@material-ui/icons/VideoCall";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import MenuIcon from "@material-ui/icons/Menu";
import { Link } from "react-router-dom";
import styled from "styled-components";
import tw from "twin.macro";
import SearchBar from "./searchBar";
import "./navbar.css";
 import useStyles from "./styles";
 import { AuthContext } from "../general/auth-context";


 const Title = styled.div`
   ${tw`
   text-2xl
  font-bold
  text-black
`};
`;
const Headers = styled.div`
  ${tw`
  text-lg
  font-semibold
  text-black
`};
`;

const Navbar = () => {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [showA, setShowA] = useState(true);
    const [showB, setShowB] = useState(true);

    const toggleShowA = () => setShowA(!showA);
    const toggleShowB = () => setShowB(!showB);

  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);
  const classes = useStyles();
  const auth = useContext(AuthContext);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };
  const openMenuHandler = (event) => setMobileMoreAnchorEl(event.currentTarget);
  const logoutHandler = (event) => {
    event.preventDefault();
    handleMobileMenuClose();
    auth.logout();
  };
  const mobileMenuId = "primary-search-account-menu-mobile";
  const renderMobileMenu = (

    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <Link to={"/chat"} style={{ textDecoration: "none" }}>
        <MenuItem onClick={handleMobileMenuClose}>
          <IconButton aria-label="Chat" color="inherit">
            <ChatBubbleIcon />
          </IconButton>
          <p>{"Chat with us"}</p>
        </MenuItem>
      </Link>
      {auth.isLoggedIn && (
        <Link to={"meeting"} style={{ textDecoration: "none" }}>
          <MenuItem onClick={handleMobileMenuClose}>
            <IconButton aria-label="Settings" color="inherit">
              <VideoCallIcon />
            </IconButton>
            <p>Arrange or View Videocall</p>
          </MenuItem>
        </Link>
      )}
      {auth.isLoggedIn && (
        <MenuItem onClick={logoutHandler}>
          <IconButton aria-label="Settings" color="inherit">
            <ExitToAppIcon />
          </IconButton>
          <p>Logout</p>
        </MenuItem>
      )}
    </Menu>

  );

   return (

     <>

     <nav class="w-screen fixed bg-white z-50 flex items-center justify-between flex-wrap bg-teal " >
      <div class="flex items-center flex-no-shrink text-white mr-6 ml-6 " >
<button class = "text-3xl bg-transparent cursor-pointer  py-2 px-4 border-none  ">
â‰¡
</button>
      </div>







      <div class="text-center lg:flex-grow ">
        <a href="#responsive-header" class="block mt-4 lg:inline-block lg:mt-0 text-teal-lighter hover:text-white mr-4">
          <img src="https://www.luxussound.com/img/logos/logo-bangolufsen.png" width="120" />
        </a>
      </div>
      <div>
        <a href="#" class="inline-block text-sm px-4 leading-none border rounded text-black border-white hover:border-transparent hover:text-teal hover:bg-white  ">
          {!auth.isLoggedIn && (
            <Link to={"/login"} style={{ textDecoration: "none" }}>
              <MenuItem onClick={handleMobileMenuClose}>
                <IconButton aria-label="Chat" color="inherit">
                  <MeetingRoomIcon />
                </IconButton>
                <button class = "text-3xl bg-transparent text-white cursor-pointer  py-2 px-4 border-none  ">
                ðŸ‘¤
                </button>
              </MenuItem>
            </Link>
          )}
       </a>

       <Row class = "text-black bg-black">
          <Col xs={6} class = " bg-black">
            <Toast onClose={() => setShow(false)} show={show} delay={3000} autohide>
              <Toast.Header  class = "bg-black">
                <img
                  src="holder.js/20x20?text=%20"
                  className="rounded me-2"
                  alt=""
                />
                <strong className="me-auto">Bootstrap</strong>
                <small >11 mins ago</small>
              </Toast.Header>
              <Toast.Body >Woohoo, you're reading this text in a Toast!</Toast.Body>
            </Toast>
          </Col>
          <Col xs={6}>
            <Button onClick={() => setShow(true)}>Show Toast</Button>
          </Col>
        </Row>






   </div>
 </nav>


       <AppBar
         position="relative"
         className={classes.appBar}
        color="inherit"
        style={{ textDecoration: "none" }}
      >
        <Toolbar>
          <Typography
            component={Link}
            to="/"
            style={{ textDecoration: "none" }}
          >
            <Title>Bang & Olufsen</Title>
          </Typography>
          <div className={classes.grow} />
          <Typography
            style={{ textDecoration: "none" }}
            component={Link}
            to={"/products"}
          >
            <Headers>All Products</Headers>
          </Typography>
          <div className={classes.grow} />
          <Typography
            style={{ textDecoration: "none" }}
            component={Link}
            to={"/stories"}
          >
            <Headers>Stories</Headers>
          </Typography>
          <div className={classes.grow} />
          <Typography
            style={{ textDecoration: "none" }}
            component={Link}
            to={"/customer"}
          >
          <Headers>Customer Service</Headers>
          </Typography>
          <div className={classes.grow} />
          <SearchBar/>
          {auth.isLoggedIn && (
            <IconButton
              aria-label="Menu"
              color="inherit"
              onClick={openMenuHandler}
            >
              <MenuIcon />
            </IconButton>
          )}
          {!auth.isLoggedIn && (
            <Link to={"/login"} style={{ textDecoration: "none" }}>
              <MenuItem onClick={handleMobileMenuClose}>
                <IconButton aria-label="Chat" color="inherit">
                  <MeetingRoomIcon />
                </IconButton>
                <Headers>{"Login"}</Headers>
              </MenuItem>
            </Link>
          )}
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
    </>
  );
};
