import React, { useState, useContext } from "react";
import {
  AppBar,
  Toolbar,
  IconButton,
  MenuItem,
  Menu,
  Typography,
} from "@material-ui/core";
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";
import VideoCallIcon from "@material-ui/icons/VideoCall";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import MenuIcon from "@material-ui/icons/Menu";
import { Link } from "react-router-dom";
import styled from "styled-components";
import tw from "twin.macro";

import SearchBar from "./searchBar";
import useStyles from "./styles";
import { AuthContext } from "../general/auth-context";

const Title = styled.div`
  ${tw`
  text-2xl
  font-bold
  text-black
`};
`;

const Headers = styled.div`
  ${tw`
  text-lg
  font-semibold
  text-black
`};
`;

const Navbar = () => {
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);
  const classes = useStyles();
  const auth = useContext(AuthContext);

  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };
  const openMenuHandler = (event) => setMobileMoreAnchorEl(event.currentTarget);

  const logoutHandler = (event) => {
    event.preventDefault();
    handleMobileMenuClose();
    auth.logout();
  };
  const [navbarOpen, setNavbarOpen] = React.useState(false);
  const mobileMenuId = "primary-search-account-menu-mobile";

  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <Link to={"/chat"} style={{ textDecoration: "none" }}>
        <MenuItem onClick={handleMobileMenuClose}>
          <IconButton aria-label="Chat" color="inherit">
            <ChatBubbleIcon />
          </IconButton>
          <p>{"Chat with us"}</p>
        </MenuItem>
      </Link>
      {auth.isLoggedIn && (
        <Link to={"meeting"} style={{ textDecoration: "none" }}>
          <MenuItem onClick={handleMobileMenuClose}>
            <IconButton aria-label="Settings" color="inherit">
              <VideoCallIcon />
            </IconButton>
            <p>Arrange or View Videocall</p>
          </MenuItem>
        </Link>
      )}
      {auth.isLoggedIn && (
        <MenuItem onClick={logoutHandler}>
          <IconButton aria-label="Settings" color="inherit">
            <ExitToAppIcon />
          </IconButton>
          <p>Logout</p>
        </MenuItem>
      )}
    </Menu>
  );

  return (
    <>



          <nav className="relative flex flex-wrap items-center justify-between px-2 py-3 bg-emerald-500 mb-3">
            <div className="container px-4 mx-auto flex flex-wrap items-center justify-between">
              <div className="w-full relative flex justify-between lg:w-auto lg:static lg:block lg:justify-start">
                <a
                  className="text-sm font-bold leading-relaxed inline-block mr-4 py-2 whitespace-nowrap uppercase text-white"
                  href="#pablo"
                >
                  emerald Tailwind Starter Kit
                </a>
                <button
                  className="text-white cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none"
                  type="button"
                  onClick={() => setNavbarOpen(!navbarOpen)}
                >
                  <i className="fas fa-bars"></i>
                </button>
              </div>
              <div
                className={
                  "lg:flex flex-grow items-center" +
                  (navbarOpen ? " flex" : " hidden")
                }
                id="example-navbar-danger"
              >
                <ul className="flex flex-col lg:flex-row list-none lg:ml-auto">
                  <li className="nav-item">
                    <a
                      className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75"
                      href="#pablo"
                    >
                      <i className="fab fa-facebook-square text-lg leading-lg text-white opacity-75"></i><span className="ml-2">Share</span>
                    </a>
                  </li>
                  <li className="nav-item">
                    <a
                      className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75"
                      href="#pablo"
                    >
                      <i className="fab fa-twitter text-lg leading-lg text-white opacity-75"></i><span className="ml-2">Tweet</span>
                    </a>
                  </li>
                  <li className="nav-item">
                    <a
                      className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75"
                      href="#pablo"
                    >
                      <i className="fab fa-pinterest text-lg leading-lg text-white opacity-75"></i><span className="ml-2">Pin</span>
                    </a>
                  </li>
                </ul>
              </div>
            </div>
          </nav>



      <AppBar
        position="relative"
        className={classes.appBar}
        color="inherit"
        style={{ textDecoration: "none" }}
      >
        <Toolbar>
          <Typography
            component={Link}
            to="/"
            style={{ textDecoration: "none" }}
          >
            <Title>Bang & Olufsen</Title>
          </Typography>
          <div className={classes.grow} />
          <Typography
            style={{ textDecoration: "none" }}
            component={Link}
            to={"/products"}
          >
            <Headers>All Products</Headers>
          </Typography>
          <div className={classes.grow} />
          <Typography
            style={{ textDecoration: "none" }}
            component={Link}
            to={"/customer"}
          >
          <Headers>Customer Service</Headers>
          </Typography>
          <div className={classes.grow} />
          <SearchBar/>
          {auth.isLoggedIn && (
            <IconButton
              aria-label="Menu"
              color="inherit"
              onClick={openMenuHandler}
            >
              <MenuIcon />
            </IconButton>
          )}
          {!auth.isLoggedIn && (
            <Link to={"/login"} style={{ textDecoration: "none" }}>
              <MenuItem onClick={handleMobileMenuClose}>
                <IconButton aria-label="Chat" color="inherit">
                  <MeetingRoomIcon />
                </IconButton>
                <Headers>{"Login"}</Headers>
              </MenuItem>
            </Link>
          )}
        </Toolbar>
      </AppBar>
      {renderMobileMenu}
    </>
  );
};

export default Navbar;
